package java19.st3studentmap;

public class Oper {
    
    public Oper() {
        super();
        // TODO Auto-generated constructor stub
    }
    public int Add(int x, int y){
        return x+y;
    }
    public int Minus(int x, int y){
        return x-y;
    }
    public int Mul(int x, int y){
        return x*y;
    }
    public double Div(int x, int y){
        return (double)x/y;
    }
}
