mapper.select:
select count(*) from book
 where 1=1
  and bookname  like #{bookname }
  and publisher like #{publisher}

select * from book
 where 1=1
  and bookname  like #{bookname }
  and publisher like #{publisher}

select * from book
 where 1=1
    and bookname   =  #{ bookname  } 
                       and bookid     =  #{ bookid    }   
  and publisher  =  #{ publisher } 
                        and price      =  #{ price     }

mapper.update:
update book
   set bookname  =  #{ set.bookname  }
      ,publisher =  #{ set.publisher }
      ,year      =  #{ set.year      }
      ,price     =  #{ set.price     }
      ,dtm       =  #{ set.dtm       }
      ,use_yn    =  #{ set.use_yn    }
      ,authid    =  #{ set.authid    }
  where 1=1
   and bookid   = #{where.bookid}
   and bookname = #{where.bookname}

mapper.delete:
delete from book 
    where 1=1
  and bookname  like #{bookname }
  and publisher like #{publisher}

mapper.select:
select * from book
 where 1=1                           
                       and bookid    =  #{ bookid }     
    and bookname  =  #{ bookname  }  
  and publisher =  #{ publisher }  
            and year      =  #{ year      }  
                        and price     =  #{ price     }  
                          and dtm       =  #{ dtm       }  
                       and use_yn    =  #{ use_yn    }  
                       and authid    =  #{ authid    }
