mapper.select:
select * from bookd
 where 1=1
  and bookname  like #{bookname }
  and publisher like #{publisher}
  and category  like #{category }
  and author like #{author}
  and price  like #{price }
  and no like #{no}

select * from bookd
 where 1=1
  and bookname  = #{bookname }
  and publisher = #{publisher}
  and category  = #{category }
  and author = #{author}
  and price  = #{price }
  and no = #{no}

mapper.update:
update bookd
   set    ,bookname  = #{set.bookname }
          ,publisher = #{set.publisher}
          ,category  = #{set.category }
                ,author    = #{set.author}
                                    ,price     = #{set.price }
  where 1=1
   and bookname  = #{where.bookname }
  and publisher = #{where.publisher}
  and category  = #{where.category }
  and author = #{where.author}
  and price  = #{where.price }
  and no = #{where.no}

mapper.delete:
delete from bookd
    where 1=1
  and bookname  = #{bookname }
  and publisher = #{publisher}
  and category  = #{category }
  and author = #{author}
  and price  = #{price }
  and no = #{no}

mapper.select:
select * from bookd
 where 1=1                           
  and bookname  = #{bookname }
  and publisher = #{publisher}
  and category  = #{category }
  and author = #{author}
  and price  = #{price }
  and no = #{no}
