mapper.select:

SELECT *
  FROM TB_Upload_File
 WHERE 1=1

 and fileNameTemp = #{fileNameTemp}


 and uploadFileNo = #{uploadFileNo}

mapper.insert:
INSERT INTO TB_Upload_File( fileNameOrig , fileNameTemp , fileSize , contentType  )
VALUES

        #{item.fileNameOrig} , #{item.fileNameTemp} , #{item.fileSize} , #{item.contentType}

mapper.delete:
DELETE FROM TB_Upload_File
 WHERE 1=1

 and fileNameTemp = #{fileNameTemp}


 and uploadFileNo = #{uploadFileNo}
