mapper.update:
UPDATE board
   SET hit=hit+1 
 WHERE bno=#{bno}

UPDATE board  


         title     = #{updateValue.title},    


         content   = #{updateValue.content  },  


         useYN     = #{updateValue.useyn    },


         updatedt  = #{updateValue.updatedt },


 WHERE  1 = 1 

      AND bno  = #{searchValue.bno}


      AND title      = #{searchValue.title}


      AND memid      = #{searchValue.memid}


      AND useYN      = #{searchValue.useyn}

UPDATE board  
       set  useYN = 0
 WHERE bno  = #{bno}

mapper.select:
SELECT bno, title, memid, updatedt, hit, commentNum FROM ( SELECT @RNUM := @RNUM + 1 AS r, a.* FROM ( SELECT a.bno bno, a.title, a.memid, a.updatedt, a.hit, count(distinct(c.commentno)) commentNum FROM (select * from board where useYN = 1 ) a LEFT OUTER JOIN (select * from comments where UseYN = 1 ) c ON a.bno = c.bno WHERE 1 = 1
AND ( title LIKE CONCAT('%', #{searchWord}, '%') OR content LIKE CONCAT('%', #{searchWord}, '%') )
GROUP BY a.bno, title, a.updatedt, hit ORDER BY bno DESC ) a, (SELECT @RNUM:=0) b ) temp WHERE r BETWEEN #{start} AND #{end}

SELECT bno, title, content, memid, hit, updatedt
FROM board
WHERE bno = #{bno}

SELECT bno, title FROM ( SELECT @RNUM := @RNUM + 1 AS r,a.* FROM (SELECT bno, title FROM board WHERE bno > #{bno}
AND ( title LIKE CONCAT('%', #{searchWord}, '%') OR content LIKE CONCAT('%', #{searchWord}, '%') )
ORDER BY bno ASC ) a , (SELECT @RNUM:=0) b ) c WHERE r = 1

SELECT bno, title FROM ( SELECT @RNUM := @RNUM + 1 AS r,a.* FROM (SELECT bno, title FROM board WHERE bno < #{bno}
AND ( title LIKE CONCAT('%', #{searchWord}, '%') OR content LIKE CONCAT('%', #{searchWord}, '%') )
ORDER BY bno DESC ) a , (SELECT @RNUM:=0) b ) c WHERE r = 1
